mapper.select:
SELECT T.* ,BS1.BUS_STATION_NAME AS ARRIVAL_STATION_NAME ,TS.TOTALSEATS-COALESCE(BOOKEDSEATS,0) AS AVAILABLE_SEATS FROM ( SELECT R.* ,BS.BUS_STATION_NAME AS DEPARTURE_STATION_NAME FROM BUS_STATION AS BS INNER JOIN ROUTE AS R ON BS.BUS_STATION_ID = R.DEPARTURE_ID ) AS T INNER JOIN BUS_STATION AS BS1 ON BS1.BUS_STATION_ID = T.ARRIVAL_ID INNER JOIN
( SELECT B.BUS_ID ,B.ROW_NUM * B.COLUMN_NUM AS TOTALSEATS FROM BUS AS B GROUP BY B.BUS_ID ORDER BY B.BUS_ID ) AS TS ON T.bus_id=TS.bus_id LEFT OUTER JOIN
( SELECT COUNT(*) AS BOOKEDSEATS ,S.BUS_ID FROM RESERVE AS RE INNER JOIN SEAT AS S ON RE.RESERVE_ID=S.RESERVE_ID WHERE departure_date = #{departureDate,jdbcType=DATE} GROUP BY S.BUS_ID ) AS BS ON TS.BUS_ID=BS.BUS_ID WHERE T.DEPARTURE_STATION_NAME LIKE #{departureStationName,jdbcType=VARCHAR} AND BS1.BUS_STATION_NAME LIKE #{arrivalStationName,jdbcType=VARCHAR} AND #{departureDate,jdbcType=DATE} BETWEEN T.OPERATION_START_DATE AND T.OPERATION_END_DATE;
